import Rhino as rc
def rcArea(c):
    if c:
        rcAreas = rc.Geometry.AreaMassProperties.Compute(c)
        return rcAreas.Area

dev_areas = []
for dev in devs:
    dev_areas.append(rcArea(dev))
plot_areas = []
for plot in plots:
    plot_areas.append(rcArea(plot))
building_areas = []
for building in buildings:
    building_areas.append(rcArea(building))
