from Rhino.Geometry import Interval
step = (end - start) / count
intervals = []
for i in range(count):
    intervals.append(Interval(start, start + step))
    start += step
a = []
for v in values:
    for i in range(len(intervals)):
        if intervals[i].IncludesParameter(v):
            a.append(i)
            break
