import scriptcontext

def DoSomething():

    key = "Hello World"
    if scriptcontext.sticky.has_key(key):
        print "The key '{}' was found in sticky".format(key)
    else:
        print "Not found, adding key '{}' to sticky".format(key)
        scriptcontext.sticky[key] = "OK"

DoSomething()
