from ghpythonlib import parallel 
def trim_with_all(a):
    for b in breps:
        if b == a: continue
        trimmed = a.Trim(b, 0.01)
        if trimmed != None and len(trimmed) > 0:
            a = trimmed[0]
    return a
result = parallel.run(trim_with_all, breps)

