import Rhino.Geometry as rg


def create_line(start, direction, length):
    vec = direction
    vec.Unitize()
    return rg.Line(start, start + vec * length)


if __name__ == "__main__":
     s = rg.Point3d(0, 0, 0)
     d = rg.Vector3d(1, 1, 0)
     l = 5.0

     a = create_line(s, d, l)
