// Import the necessary RhinoCommon libraries
using Rhino;
using Rhino.Geometry;
using Rhino.DocObjects;
using Rhino.Commands;
using Rhino.Input;
using Rhino.Input.Custom;

namespace MyRhinocommonScripts
{
    public class FindTangentSphere : Command
    {
        public override string EnglishName { get { return "FindTangentSphere"; } }

        protected override Result RunCommand(RhinoDoc doc, RunMode mode)
        {
            // Create a new Rhino.Input.Custom.GetObject object to get the four surfaces from the user
            var go = new GetObject();
            go.SetCommandPrompt("Select four nurbs surfaces to find tangent sphere");
            go.GeometryFilter = ObjectType.Surface;
            go.EnablePreSelect(false, true);
            go.GetMultiple(4, 4);
            if (go.CommandResult() != Result.Success)
                return go.CommandResult();

            // Get the four selected surfaces
            var surfaces = new NurbsSurface[4];
            for (int i = 0; i < 4; i++)
            {
                var objRef = go.Object(i);
                surfaces[i] = objRef.Surface();
