import rhinoscriptsyntax as rs
import Rhino.Geometry as gh

pt1=[]
pt2=[]
pt3=[]
ptsX=[]
ptsY=[]

for x in range(0,xSize,1):
    for y in range(0,ySize,1):
        pt=rs.AddPoint(x,y,0)
        if x==0:
            ptsY.append(pt)
        if y==0:
            ptsX.append(pt)
        if x%numX==0 and y%numY==0:
            pt1.append(pt)
        elif x%numX==0 or y%numY==0: # but num1: 4%6=2,5%6=1 
            pt2.append(pt)
        else:
            pt3.append(pt)
