import Rhino
import rhinoscriptsyntax as rs


def SelQuadMesh():
    ids = rs.ObjectsByType(32,False, 1)
    if not ids: return

    geos = [rs.coercemesh(id) for id in ids]

    for i in range(len(geos)):
        sel= True
        for face in geos[i].Faces:
            if face.IsTriangle:
                sel= False
                break
        if sel:
            rs.SelectObject(ids[i])

if __name__ == "__main__":
    SelQuadMesh()

