import rhinoscriptsyntax as rs
somenumber = rs.GetReal("Line length")
try:
    line = rs.AddLine( (0,0,0), (somenumber,0,0) )
except Exception as ex:
    print (ex)
    print("Something went wrong")
else:
    print("Line curve inserted with id", line)

