import System
import Rhino
import Grasshopper

import rhinoscriptsyntax as rs

class MyComponent(Grasshopper.Kernel.GH_ScriptInstance):
    def RunScript(self, x, y, z):
        if x:
            a = x
        if a and y:
            a = a + y
        if a and z:
            a = a + z
        return a
