public class Loading : GH_AssemblyPriority
{
    public override GH_LoadingInstruction PriorityLoad()
    {
        RhinoDoc.AddRhinoObject += RhinoDocOnAddRhinoObject;
        return GH_LoadingInstruction.Proceed;
    }

    private static void RhinoDocOnAddRhinoObject(object sender, RhinoObjectEventArgs e)
    {
        var objectType = e.TheObject.ObjectType.ToString();
        RhinoApp.WriteLine(
            $"I saw what you're up to! You just added a new {objectType} to the document. Watch out—I’m keeping an eye on you!");
    }
}
