#! python 3
import Rhino
import scriptcontext as sc

def Test():
    rc, point = Rhino.Input.RhinoGet.GetPoint("Location of point object", False)
    if rc == Rhino.Commands.Result.Success:
        sc.doc.Objects.AddPoint(point)
        sc.doc.Views.Redraw()

if __name__ == "__main__":    
    Test()
