#! python 2
import scriptcontext

def DoSomething():

    view_names = scriptcontext.doc.NamedViews.Selection
    if not view_names: return

    for view_name in view_names:
        view_index = scriptcontext.doc.NamedViews.FindByName(view_name)
        view_info = scriptcontext.doc.NamedViews.Item[view_index]

        print "ViewName: {} >>> view_info: {}".format(view_name, view_info)

DoSomething()

#! python 3
import scriptcontext

def DoSomething():

    view_names = scriptcontext.doc.NamedViews.Selection
    if not view_names: return

    for view_name in view_names:
        view_index = scriptcontext.doc.NamedViews.FindByName(view_name)
        view_info = scriptcontext.doc.NamedViews[view_index]

        print("ViewName: {} >>> view_info: {}".format(view_name, view_info))

DoSomething()
