def curve_CL(curves):
    points = []
    plane = rs.WorldZXPlane()
    for crv in curves:
        int = rs.PlaneCurveIntersection(plane,crv)
        if int:
            points.append(int[1])
        else:            
            print "Error with plane-curve intersection"
    return points

def curve_CL(curves):
    points = []
    plane = rs.WorldZXPlane()
    for crv in curves:
        int = rs.PlaneCurveIntersection(plane,crv)
        if int:
            for i in range(len(int)):
                points.append(int[i][1])
        else:            
            print "Error with plane-curve intersection"
    return points

def curve_CL(curves):
    points = []
    plane = rs.WorldZXPlane()
    for crv in curves:
        int = rs.PlaneCurveIntersection(plane,crv)
        if not int:
            print "Error with plane-curve intersection"
        points.append(int[1])
    return points
