import rhinoscriptsyntax as rs

def DisplayOrder():
    objects = rs.GetObjects("Select the objects that are on top", 0, None, True, True)
    if objects:
        copied = rs.CopyObjects(objects)
        if copied:
            rs.DeleteObjects(objects)
        rs.UnselectAllObjects()
        rs.Redraw()

if __name__ == "__main__":
    DisplayOrder()

import rhinoscriptsyntax as rs

def DisplayOrder():
    objects = rs.GetObjects("Select the objects that are displayed on top")
    if objects:
        copied = rs.CopyObject(objects)
        if copied:
            rs.DeleteObjects(objects)
        rs.UnselectAllObjects()
        rs.Redraw()

if __name__ == "__main__":
    DisplayOrder()
