def OffsetBrep():

    id = rs.GetObject(preselect=True)
    brep= rs.coercebrep(id)
    oBreps = Rhino.Geometry.Brep.CreateOffsetBrep( brep, 2, False, True, .001)

    for brepList in oBreps:
        for x in brepList:
            if x.IsValid:
                sc.doc.Objects.AddBrep(x)

    rs.Redraw()
