import rhinoscriptsyntax as rs

ids = rs.GetObjects(filter=4096, preselect=True)

if ids:
    for id in ids: 
        rs.InsertBlock(rs.BlockInstanceName(id), [0,0,0])
        rs.DeleteObject(id)

import rhinoscriptsyntax as rs

ids = rs.GetObjects(filter=4096, preselect=True)

if ids:
    for id in ids: 
        rs.InsertBlock(rs.BlockInstanceName(id), rs.BlockInstanceInsertPoint(id))
        rs.DeleteObject(id)
