import Rhino

class Point3d:
    def __init__(self):
        self.x = 0.0
        self.y = 0.0
        self.z = 0.0
        self.usertext = None

    def set_usertext(self, usertext):
        self.usertext = usertext

    def get_point(self):
        return Rhino.Geometry.Point3d(self.x, self.y, self.z)

pt = Point3d()
