from ghpythonlib import treehelpers

def split(lt, size=2, out_tree=False):
    nested_lt = []

    while len(lt) > size:
        seg = lt[:size]
        nested_lt.append(seg)
        lt   = lt[size:]
    nested_lt.append(lt)

    if not out_tree:
        return nested_lt

    return treehelpers.list_to_tree(nested_lt)
