import rhinoscriptsyntax as rs
curve = rs.GetObject("Select closed curve", 4)
if curve:
    area_data = rs.CurveArea(curve)
    if area_data:
        print("Area:", area_data[0])

import rhinoscriptsyntax as rs
obj = rs.GetObject("Select closed polysurface", 16)
if obj:
    vol = rs.SurfaceVolume(obj)
    if vol:
        print("Volume:", vol[0])

import rhinoscriptsyntax as rs
c1 = rs.GetObject("Select first curve", 4)
c2 = rs.GetObject("Select second curve", 4)
if c1 and c2:
    intersections = rs.CurveCurveIntersection(c1, c2)
    if intersections:
        for ccx in intersections:
            rs.AddPoint(ccx[1])
        print("Found", len(intersections), "intersections")
    else:
        print("No intersections found")

import rhinoscriptsyntax as rs
curve = rs.GetObject("Select curve", 4)
srf = rs.GetObject("Select surface", 8)
if curve and srf:
    pts = rs.CurveSurfaceIntersection(curve, srf)
    if pts:
        for item in pts:
            rs.AddPoint(item[1])
        print("Found", len(pts), "intersection(s)")

import rhinoscriptsyntax as rs
curve = rs.GetObject("Select closed curve", 4)
if curve:
    pt = rs.GetPoint("Pick a point")
    if pt:
        result = rs.PointInPlanarClosedCurve(pt, curve)
        if result == 0:
            print("Point is outside")
        elif result == 1:
            print("Point is inside")
        else:
            print("Point is on the curve")

import rhinoscriptsyntax as rs
curve = rs.GetObject("Select curve", 4)
if curve:
    params = rs.DivideCurve(curve, 20, create_points=False)
    for t in params:
        data = rs.CurveCurvature(curve, t)
        if data and data[3] > 0:
            radius = min(data[3], 50)
            plane = rs.CurvePerpFrame(curve, t)
            rs.AddCircle(plane, radius * 0.1)
