import rhinoscriptsyntax as rs
obj = rs.GetObject("Select object to array")
if obj:
    for i in range(1, 5):
        copy = rs.CopyObject(obj, (i * 12, 0, 0))

import rhinoscriptsyntax as rs
obj = rs.GetObject("Select object")
if obj:
    for i in range(1, 8):
        copy = rs.CopyObject(obj)
        rs.RotateObject(copy, (0,0,0), i * (360.0/8))

import rhinoscriptsyntax as rs
curve = rs.GetObject("Select closed curve", 4)
if curve and rs.IsCurveClosed(curve):
    centroid = rs.CurveAreaCentroid(curve)[0]
    for i in range(1, 6):
        rs.OffsetCurve(curve, centroid, i * 2)

import rhinoscriptsyntax as rs
curves = rs.GetObjects("Select curves to join", 4)
if curves:
    joined = rs.JoinCurves(curves)
    if joined:
        print("Joined into", len(joined), "curve(s)")

import rhinoscriptsyntax as rs
obj = rs.GetObject("Select polysurface", 16)
if obj:
    edges = rs.DuplicateEdgeCurves(obj)
    if edges:
        rs.FilletSrf(obj, 1.0)

import rhinoscriptsyntax as rs
points = rs.GetObjects("Select points", 1)
srf = rs.GetObject("Select surface", 8)
if points and srf:
    for pt_id in points:
        pt = rs.PointCoordinates(pt_id)
        projected = rs.ProjectPointToSurface(pt, srf, (0,0,-1))
        if projected:
            rs.AddPoint(projected[0])
