import rhinoscriptsyntax as rs
for layer in rs.LayerNames():
    if rs.IsLayerEmpty(layer) and not rs.IsLayerCurrent(layer):
        rs.DeleteLayer(layer)

import rhinoscriptsyntax as rs
objs = rs.GetObjects("Select objects")
if objs:
    if not rs.IsLayer("Final"):
        rs.AddLayer("Final")
    for obj in objs:
        rs.ObjectLayer(obj, "Final")

import rhinoscriptsyntax as rs
obj = rs.GetObject("Select grouped object")
if obj:
    grp = rs.ObjectGroups(obj)
    if grp:
        rs.DeleteGroup(grp[0])

import rhinoscriptsyntax as rs
names = rs.BlockNames()
if names:
    pt = rs.GetPoint("Insertion point")
    if pt:
        rs.InsertBlock(names[0], pt)

import rhinoscriptsyntax as rs
objs = rs.GetObjects("Select objects for block")
if objs:
    base = rs.GetPoint("Base point")
    if base:
        rs.AddBlock(objs, base, "MyBlock", True)

import rhinoscriptsyntax as rs
for name in rs.BlockNames():
    if rs.BlockInstanceCount(name) == 0:
        rs.DeleteBlock(name)
        print("Deleted block:", name)

import rhinoscriptsyntax as rs
objs = rs.ObjectsByLayer("Walls")
if objs:
    for obj in objs:
        rs.ObjectPrintWidth(obj, 0.5)
