srf = rs.GetObject('Select surface', 8)
if srf:
    bb = rs.BoundingBox(srf)
    if bb:
        z_min = bb[0][2]
        z_max = bb[4][2]
        n = 10
        for i in range(n + 1):
            z = z_min + (z_max - z_min) * i / n
            contours = rs.AddSrfContourCrvs(srf, (0,0,z), (0,0,z+0.01))
            if contours:
                for c in contours:
                    rs.ObjectColor(c, (0, int(255*i/n), int(255*(1-i/n))))
