import rhinoscriptsyntax as rs
pts = rs.ObjectsByType(1)
if pts:
    coords = [(p, rs.PointCoordinates(p)) for p in pts]
    dupes = []
    for i in range(len(coords)):
        for j in range(i+1, len(coords)):
            if rs.Distance(coords[i][1], coords[j][1]) < 0.01:
                dupes.append(coords[j][0])
    if dupes:
        rs.DeleteObjects(dupes)
        print(f'Removed {len(dupes)} duplicate points')

