import rhinoscriptsyntax as rs
crv = rs.GetObject('Select curve', 4)
if crv:
    dom = rs.CurveDomain(crv)
    mid = (dom[0] + dom[1]) / 2.0
    parts = rs.SplitCurve(crv, mid)
    if parts:
        rs.ObjectColor(parts[0], (255,0,0))
        if len(parts) > 1:
            rs.ObjectColor(parts[1], (0,0,255))

