import rhino3dm
model = rhino3dm.File3dm()
for i in range(5):
    for j in range(5):
        model.Objects.AddPoint(rhino3dm.Point3d(i*2, j*2, 0))
model.Write('grid.3dm', 7)

import rhino3dm
model = rhino3dm.File3dm.Read('input.3dm')
for obj in model.Objects:
    name = obj.Attributes.Name
    if name:
        print(f'Named object: {name}')

import rhino3dm
model = rhino3dm.File3dm()
mesh = rhino3dm.Mesh()
rows, cols = 10, 10
for i in range(rows+1):
    for j in range(cols+1):
        mesh.Vertices.Add(j, i, 0)
for i in range(rows):
    for j in range(cols):
        a = i*(cols+1)+j
        mesh.Faces.AddFace(a, a+1, a+cols+2, a+cols+1)
model.Objects.AddMesh(mesh)
model.Write('quad_plane.3dm', 7)

import rhino3dm
import os
results = {}
for fname in os.listdir('models/'):
    if fname.endswith('.3dm'):
        model = rhino3dm.File3dm.Read(os.path.join('models/', fname))
        count = len(model.Objects)
        results[fname] = count
        print(f'{fname}: {count} objects')
