def menger(x, y, z, size, level):
    if level == 0:
        corners = [
            (x,y,z),(x+size,y,z),(x+size,y+size,z),(x,y+size,z),
            (x,y,z+size),(x+size,y,z+size),(x+size,y+size,z+size),(x,y+size,z+size)
        ]
        rs.AddBox(corners)
        return
    s = size / 3.0
    for i in range(3):
        for j in range(3):
            for k in range(3):
                if [i,j,k].count(1) < 2:
                    menger(x+i*s, y+j*s, z+k*s, s, level-1)

